    {
      id: 1,
      name: 'Ava Olivia',
      date: '24/04/2024',
      time: '10:00 AM',
      status: 'scheduled'
    },
    {
      id: 2,
      name: 'Budi Santoso',
      date: '25/04/2024',
      time: '14:00 PM',
      status: 'scheduled'
    },
    {
      id: 3,
      name: 'Citra Dewi',
      date: '26/04/2024',
      time: '09:00 AM',
      status: 'scheduled'
    }
